package com.gildedrose;public class Common extends Item{    public Common(String name, int sellIn, int quality) {        super(name, sellIn, quality);    }    @Override    public void updateSellIn() {        super.updateSellIn();    }    @Override    public void updateQuality() {        if(sellIn>0){            if(quality>0){                quality--;            }        }else {            if(quality>1){                quality=quality-2;            }else {                quality=0;            }        }    }}