package com.gildedrose;public class Conjured extends Item {    public Conjured(int sellIn, int quality) {        super("Conjured Mana Cake", sellIn, quality);    }    @Override    public void updateSellIn() {        sellIn=sellIn-1;    }    @Override    public void updateQuality() {        if(sellIn>0){            if(quality>2){                quality=quality-2;            }else {                quality=0;            }        }else {            if(quality>4){                quality=quality-4;            }else {               quality=0;            }        }    }}