package com.gildedrose;import org.junit.jupiter.api.Test;import static org.hamcrest.core.Is.is;import static org.junit.Assert.assertThat;class AgedBrieTest {    @Test    void should_quality_equals_50_and_sellIn_decr_by_1_when_update_given_sellIn_between_negative51_0_and_quality_equals_49() {            for (int sellIn = -51; sellIn <= 0; sellIn++) {                int oldSellIn = sellIn;                Item item = new AgedBrie(sellIn, 49);                item.passOneDay();                assertThat(item.quality,is(50));                assertThat(item.sellIn,is(oldSellIn-1));            }    }    @Test    void should_quality_equals_50_and_sellIn_decr_by_1_when_update_given_sellIn_between_1_51_and_quality_equals_50() {            for (int sellIn = 1; sellIn <= 51; sellIn++) {                int oldSellIn = sellIn;                Item item = new AgedBrie(oldSellIn, 50);                item.passOneDay();                assertThat(item.quality,is(50));                assertThat(item.sellIn,is(oldSellIn-1));            }    }    @Test    void should_quality_incr_by_1_and_sellIn_decr_by_1_when_update_given_sellIn_between_1_51_and_quality_between_1_49()        {            for (int quality = 1; quality <= 49; quality++) {                for (int sellIn = 1; sellIn <= 51; sellIn++) {                    int oldSellIn = sellIn, oldQuality = quality;                    Item item = new AgedBrie(oldSellIn, quality);                    item.passOneDay();                    assertThat(item.quality,is(oldQuality+1));                    assertThat(item.sellIn,is(oldSellIn-1));                }            }        }    @Test    void should_quality_incr_by_2_and_quality_not_bigger_than_50_and_sellIn_decr_by_1_when_update_given_sellIn_between_negative51_0_and_quality_between_0_48() {        for (int quality = 0; quality <= 48; quality++) {            for (int sellIn = -51; sellIn <= 0; sellIn++) {                int oldSellIn = sellIn, oldQuality = quality;                Item item = new AgedBrie(oldSellIn, quality);                item.passOneDay();                assertThat(item.quality,is(oldQuality + 2));                assertThat(item.sellIn,is(oldSellIn-1));            }        }    }}