package com.gildedrose;import org.junit.jupiter.api.Test;import static org.junit.jupiter.api.Assertions.*;class AgedBrieTest {    @Test    void should_quality_incr_by_1_and_sellIn_decr_by_1_when_update_given_in_sell_and_quality_less_than_50(){        Item item=new AgedBrie(20,45);        item.updateSellIn();        item.updateQuality();        assert(item.quality==46);        assert(item.sellIn==19);    }    @Test    void should_quality_equals_50_and_sellIn_decr_by_1_when_update_given_in_sell_and_quality_equals_50(){        Item item=new AgedBrie(20,50);        item.updateSellIn();        item.updateQuality();        assert(item.quality==50);        assert(item.sellIn==19);    }    @Test    void should_quality_incr_by_2_and_sellIn_decr_by_1_when_update_given_out_sell_and_quality_less_than_49(){        Item item=new AgedBrie(-3,45);        item.updateSellIn();        item.updateQuality();        assert(item.quality==6);        assert(item.sellIn==-4);    }    @Test    void should_quality_equals_50_and_sellIn_decr_by_1_when_update_given_out_sell_and_quality_equals_49(){        Item item=new AgedBrie(-1,49);        item.updateSellIn();        item.updateQuality();        assert(item.quality==50);        assert(item.sellIn==-2);    }}