package com.gildedrose;import org.junit.jupiter.api.Test;import static org.junit.jupiter.api.Assertions.*;class BackstagePassTest {    @Test    void should_quality_incr_by_1_and_sellIn_decr_by_1_when_update_given_in_sell_above_10_and_quality_less_than_50(){        Item item=new AgedBrie(14,40);        item.updateSellIn();        item.updateQuality();        assert(item.quality==41);        assert(item.sellIn==13);    }    @Test    void should_quality_equals_50_and_sellIn_decr_by_1_when_update_given_in_sell_above_10_and_quality_equals_50(){        Item item=new BackstagePass(14,50);        item.updateSellIn();        item.updateQuality();        assert(item.quality==50);        assert(item.sellIn==13);    }    @Test    void should_quality_incr_by_2_and_sellIn_decr_by_1_when_update_given_in_sell_between_5_10_and_quality_less_than_49(){        Item item=new BackstagePass(7,47);        item.updateSellIn();        item.updateQuality();        assert(item.quality==49);        assert(item.sellIn==6);    }    @Test    void should_quality_equals_50_and_sellIn_decr_by_1_when_update_given_in_sell_between_5_10_and_quality_equals_49(){        Item item=new BackstagePass(7,49);        item.updateSellIn();        item.updateQuality();        assert(item.quality==50);        assert(item.sellIn==6);    }    @Test    void should_quality_incr_by_3_and_sellIn_decr_by_1_when_update_given_in_sell_equals_5_and_quality_less_than_48(){        Item item=new BackstagePass(5,46);        item.updateSellIn();        item.updateQuality();        assert(item.quality==49);        assert(item.sellIn==4);    }    @Test    void should_quality_equals_0_and_sellIn_decr_by_1_when_update_given_in_expired_and_quality_between_0_50(){        Item item=new BackstagePass(0,46);        item.updateSellIn();        item.updateQuality();        assert(item.quality==0);        assert(item.sellIn==-1);    }}