package com.gildedrose;import org.junit.jupiter.api.Test;import static org.junit.jupiter.api.Assertions.*;class ConjuredTest {    @Test    void should_quality_equals_0_and_sellIn_decr_by_1_when_update_given_in_sell_above_0_and_quality_between_0_2(){        Item item=new Conjured(5,1);        item.updateSellIn();        item.updateQuality();        assert(item.quality==0);        assert(item.sellIn==4);    }    @Test    void should_quality_decs_by_2_and_sellIn_decr_by_1_when_update_given_in_sell_above_0_and_quality_above_2(){        Item item=new Conjured(4,49);        item.updateSellIn();        item.updateQuality();        assert(item.quality==47);        assert(item.sellIn==3);    }    @Test    void should_quality_incr_by_4_and_sellIn_decr_by_1_when_update_given_expired(){        Item item=new Conjured(-1,40);        item.updateSellIn();        item.updateQuality();        assert(item.quality==36);        assert(item.sellIn==-2);    }}