package com.gildedrose;import org.junit.jupiter.api.Test;import static org.hamcrest.core.Is.is;import static org.junit.Assert.assertThat;import static org.junit.jupiter.api.Assertions.*;class SulfurasTest {    @Test    void should_quality_not_change_and_sellIn_not_change_when_update_given_sellIn_between_negative51_51(){            for(int sellIn = -51;sellIn<=51;sellIn++){                int oldSellIn=sellIn;                Item item=new Sulfuras(sellIn,80);                item.passOneDay();                assertThat(item.quality,is(80));                assertThat(item.sellIn,is(oldSellIn));            }    }}