package com.gildedrose;import org.junit.jupiter.api.Test;import static org.junit.jupiter.api.Assertions.*;class SulfurasTest {    @Test    void should_quality_not_change_and_sellIn_not_change_when_update_given_in_sell_above_0(){        Item item=new Sulfuras(5,80);        item.updateSellIn();        item.updateQuality();        assert(item.quality==80);        assert(item.sellIn==5);    }    @Test    void should_quality_not_change_and_sellIn_not_change_when_update_given_expired(){        Item item=new Sulfuras(-1,80);        item.updateSellIn();        item.updateQuality();        assert(item.quality==80);        assert(item.sellIn==-1);    }}